mapper.select:
SELECT 

FROM edu_publisher a

WHERE a.id = #{id}

SELECT 

FROM edu_publisher a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM edu_publisher a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE edu_publisher SET 	
	name = #{name},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE edu_publisher SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
